import React, { useState } from "react";

export default function DemoState() {
  // [ state, setState]
  // state ~ giá trị : quản lý các giá trị ảnh hưởng đến giao diện
  // setState ~ function : dùng để update giá trị của state
  // sau khi dùng setState thì component sẽ render lại
  let [number, setNumber] = useState(100);
  let handleUp = () => {
    console.log("Up");
    setNumber(number + 1);
    console.log("number:", number);
  };
  console.log("render");
  let value = 10;
  return (
    <div>
      <h2>DemoState</h2>
      <h1>{++value}</h1>
      <h2>{number}</h2>
      <button onClick={handleUp} className="btn btn-danger">
        Up
      </button>
    </div>
  );
}
