import React, { useState } from "react";
import ButtonChangeSize from "./ButtonChangeSize";

export default function ExPropsChangeSize() {
  const [fontSize, setFontSize] = useState(30);
  return (
    <div>
      <p
        style={{
          fontSize: fontSize,
        }}
        className="text-danger"
      >
        hello mọi người
      </p>
      <ButtonChangeSize
        setFontSize={setFontSize}
        fontSize={fontSize}
        type={"tru"}
      />

      <ButtonChangeSize
        setFontSize={setFontSize}
        fontSize={fontSize}
        type={"cong"}
      />

      <ButtonChangeSize
        setFontSize={setFontSize}
        fontSize={fontSize}
        type={"nhan"}
      />
    </div>
  );
}

