import React, { useState } from "react";
import { movieArr } from "./data";
import CardMovieItem from "./CardMovieItem";

export default function Ex_RenderMovie() {
  const [selectedId, setSelectedId] = useState([]);
  let handleClick = (id) => {
    let newSelectedId = [...selectedId, id];
    setSelectedId(newSelectedId);
  };
  let renderMovie = () => {
    return movieArr.map((movie) => {
      return (
        <CardMovieItem
          phim={movie}
          handleClick={handleClick}
          selectedId={selectedId}
        />
      );
    });
  };
  console.log("selectedId:", selectedId);
  return (
    <div>
      <h2>Ex_RenderMovie</h2>
      <div className="row">{renderMovie()}</div>
    </div>
  );
}
