import React, { Fragment } from "react";

export default function BindingData() {
  let title = "Hello BC72";
  let imgSrc =
    "https://www.cofeapp.com/wp-content/uploads/2022/07/bean-3-2.webp";
  let renderButton = () => {
    return <button className="btn btn-dark">Click me</button>;
  };
  let handleClickHello = () => {
    console.log("Hello");
  };
  let handleHelloWithName = (name) => {
    console.log("Hello ", name);
  };
  // với hàm có tham số, cần phải sử dụng arrow function để bọc lại khi binding
  // thẻ Fragment sẽ biến mất khi render, thường dùng để bọc nhiều thẻ con
  return (
    <Fragment>
      <h2>BindingData</h2>
      <h1>{title}</h1>
      <img src={imgSrc} alt="" />
      {renderButton()}
      <button onClick={handleClickHello}>Click Hello</button>
      <button
        onClick={() => {
          handleHelloWithName("Alice");
        }}
      >
        Click Hello Alice
      </button>
    </Fragment>
  );
}
