import React, { useState } from "react";

// inline style : object
export default function ConditionalRendering() {
  // isShowImg
  let [isShow, setIsShow] = useState(false);
  let imgSrc =
    "https://salt.tikicdn.com/ts/tikimsp/9a/07/39/c0abf22728b308b8f0b2c56ae08c5f04.png";

  let renderImg = () => {
    if (isShow) {
      return <img style={{ width: 200 }} src={imgSrc} alt="" />;
    }
    return <>áº¨n</>;
  };
  let handleShow = () => {
    setIsShow(true);
  };
  let handleHide = () => setIsShow(false);
  return (
    <div>
      <h2 className="title">ConditionalRendering</h2>
      {/* {renderImg()} */}
      {/* {isShow ? <img style={{ width: 200 }} src={imgSrc} alt="" /> : null} */}
      {isShow && <img style={{ width: 200 }} src={imgSrc} alt="" />}
      <button
        onClick={handleShow}
        className={`btn btn-dark  ${isShow ? "d-none" : ""}`}
      >
        Show
      </button>
      <button onClick={handleHide} className="btn btn-danger">
        Hide
      </button>
    </div>
  );
}
