import React from "react";

const buttonTypeContent = {
  cong: "Cộng",
  nhan: "Nhân",
  tru: "Trừ",
};

const buttonTypeClass = {
  cong: "btn btn-success",
  nhan: "btn btn-primary",
  tru: "btn btn-danger",
};

export default function ButtonChangeSize({ setFontSize, fontSize, type }) {
  return (
    <button
      onClick={() => {
        if (type === "tru") {
          setFontSize(fontSize - 1);
        } else if (type === "cong") {
          setFontSize(fontSize + 1);
        } else if (type === "nhan") {
          setFontSize(fontSize * 2);
        }
      }}
      className={buttonTypeClass[type]}
    >
      {buttonTypeContent[type]}
    </button>
  );
}
