import React from "react";
import ItemCardMovie from "./ItemCardMovie";

// props là tham số có sẵn của function component dùng để nhận giá trị từ component cha truyền vào component con

// kiểu dữ liệu props chuyền xuống có thể là : string , number , object , array , function

export default function DemoProps() {
  const infoMovie = {
    content: "Phim hay lắm",
    ngayChieu: "2024-08-01",
  };

  const infoMovie1 = {
    content: "Phim hay lắm 1",
    ngayChieu: "2024-12-01",
  };

  const sayHello = (nameMovie) => {
    console.log("hello mọi người", nameMovie);
  };

  return (
    <div className="d-flex">
      <ItemCardMovie
        tenPhim={"Đô rê mon"}
        hinhAnh={"https://movienew.cybersoft.edu.vn/hinhanh/insideout.jpg"}
        soSao={5}
        thongTinPhim={infoMovie}
        sayHello={sayHello}
      />
      <ItemCardMovie
        tenPhim={"Connan"}
        hinhAnh={"https://movienew.cybersoft.edu.vn/hinhanh/home.jpg"}
        soSao={10}
        thongTinPhim={infoMovie1}
        sayHello={sayHello}
      />
      <ItemCardMovie
        sayHello={sayHello}
        tenPhim={"one pice"}
        hinhAnh={
          "https://movienew.cybersoft.edu.vn/hinhanh/batmanvssuperman.jpg"
        }
        soSao={7}
      />
    </div>
  );
}
