import React from "react";

export default function CardMovieItem({ phim, handleClick, selectedId }) {
  return (
    <div
      className="col-2 p-2 rounded-2"
      style={{
        backgroundColor: selectedId.includes(phim.maPhim) ? "red" : "white",
      }}
      key={phim.maPhim}
      onClick={() => {
        handleClick(phim.maPhim);
      }}
    >
      <img src={phim.hinhAnh} height={200} width={"100%"} alt="" />
      <h6>{phim.tenPhim}</h6>
    </div>
  );
}
