import React, { useState } from "react";

export default function RenderWithMap() {
  // ush
  const [colors, setColors] = useState(["red", "green", "blue", "black"]);
  //   click vào item để xoá khỏi giao diện
  const handleDelete = (item) => {
    console.log("item:", item);
    let newColors = colors.filter((color) => color !== item);
    //   setState => render lại giao diện
    setColors(newColors);
  };
  let renderListColor = () => {
    return colors.map((colorItem, index) => {
      return (
        <div
          key={index}
          onClick={() => {
            handleDelete(colorItem);
          }}
          style={{
            width: 100,
            height: 100,
            backgroundColor: colorItem,
          }}
        ></div>
      );
    });
  };
  return (
    <div>
      <h2>RenderWithMap</h2>
      {renderListColor()}
    </div>
  );
}
